          </div>
        </div>
      </div>

      <div style={styles.rightPanel}>
        <div style={styles.formContainer}>
          {step === 1 && renderStep1()}
          {step === 2 && renderStep2()}
          {step === 3 && renderStep3()}

          <div style={styles.loginSection}>
            <p style={styles.loginText}>
              Already have an account?{' '}
              <a href="/login" style={styles.loginLink}>
                Sign in
              </a>
            </p>
          </div>
        </div>
      </div>
    </div>
  );
};

